import * as types from '../constants/ActionTypes'


const receiveProducts = products => ({
  type: types.RECEIVE_PRODUCTS,
  products
})
/*
var options = {
  enableHighAccuracy: true,
  timeout: 5000,
  maximumAge: 0
};

function success(pos) {
  var crd = pos.coords;

  console.log('Your current position is:');
  console.log('Latitude : ' + crd.latitude);
  console.log('Longitude: ' + crd.longitude);
  console.log('More or less ' + crd.accuracy + ' meters.');
};

function error(err) {
  console.warn('ERROR(' + err.code + '): ' + err.message);
};
*/

export const getGeolocation = () => async dispatch => 
{ 
    
    const geolocation = navigator.geolocation; 
    geolocation.getCurrentPosition ( position => 
    { 
      dispatch({ type: types.GET_LOCATION, payload: position.coords });     
    }, error => 
    { 
      if (error.code === 1) 
      { 
          dispatch({ type: types.GEOLOCATION_FAILED, payload: false }); 
      } 
    } 
    ); 
};



export const  getAllProducts =  () => dispatch => {

   
    fetch("https://ww4.cuponatic.com/get?c=Santiago&latitud=-33.45774340&longitud=-70.65735780&")
     .then(
        response => response.json(),
        error => console.log('An error occurred.', error)
      )
     .then(json =>
          dispatch(receiveProducts(json))
     )
}

const chooseTypeSearchUnsafe = typechoose => ({
  type: types.CHOOSE_TYPE_SEARCH,
  typechoose
})

export const chooseTypeSearch = type => (dispatch) => {
    dispatch(chooseTypeSearchUnsafe(type))
}

const addToCartUnsafe = productId => ({
  type: types.ADD_TO_CART,
  productId
})

export const addToCart = productId => (dispatch) => {
    dispatch(addToCartUnsafe(productId));
}

const deleteToCartUnsafe = productId => ({
  type: types.DELETE_TO_CART,
  productId
})

export const deleteToCart = productId => (dispatch) => {
  dispatch(deleteToCartUnsafe(productId))
}

export const checkout = products => (dispatch) => {
  dispatch({
    type: types.CHECKOUT_REQUEST
  })
 
}
