import { getTotal } from '../reducers'
import { getProduct } from '../reducers/products'
import { connect } from 'react-redux'
import { ReactReduxContext } from 'react-redux'


export const addToLocalStorageCart =  (value) => {

	var existing = localStorage.getItem("cart3");

	// If no existing data, create an array
	// Otherwise, convert the localStorage string to an array
	existing = existing ? JSON.parse(existing) : {};
	//console.log(existing);

	let key=300;
	// Add new data to localStorage Array
	existing[value] = (existing[value] || 0) + 1;

	// Save back to localStorage
	localStorage.setItem("cart3", JSON.stringify(existing));
	console.log(getLocalStorageCart());
	

};

export const getLocalStorageCart =  () => {

	var id = localStorage.getItem("cart3");
	id = id ? JSON.parse(id) : {};
	return id;
	
	/*console.log(f);
	Object.entries(existing).map(([key, value]) => {
        console.log(key)
        console.log(value)
      
    })*/

};

const mapStateToProps = (state) => ({
	products: getProduct(state.products),
	total: getTotal(state)
  })
  
  export default connect(
	mapStateToProps
  )(getLocalStorageCart)
