import React, { Component } from 'react'
import ReactDOM from 'react-dom';
//import { render } from 'react-dom'
import { createStore, applyMiddleware } from 'redux'
import { Provider } from 'react-redux'
import { createLogger } from 'redux-logger'
import thunk from 'redux-thunk'
import reducer from './reducers'
import { getAllProducts, getGeolocation } from './actions'
import App from './containers/App'
import Pay from './containers/Pay'
import {BrowserRouter, Switch, Route} from 'react-router-dom';
import { connect } from 'react-redux'
//import { getLocation } from '../src/reducers/products'

import { addToCart } from './actions'
import { getVisibleProducts, getType, getLocation } from './reducers/products'
import ProductItem from './src/components/ProductItem'


const middleware = [ thunk ];
if (process.env.NODE_ENV !== 'production') {
  middleware.push(createLogger());
}

// crea el store de redux
const store = createStore(
  reducer,
  applyMiddleware(...middleware)
)
// cargo la localización del usuario
store.dispatch(getGeolocation())
store.dispatch(getAllProducts())
/*
const Locations = ({location}) => (

   console.log(location)

)


// cargo todos los productos



// map redux storage to local state
const mapStateToProps = state => ({
  location: getLocation(state)
})

connect(
  mapStateToProps 
)(Locations)*/


const ProductsContainer = ({ products,addToCart,  type, loc }) => (
  
  //console.log(loc),
  //products=  products.sort(dynamicSort(type)),
  <div className="row flex-row">
    {products.map(product =>
      <ProductItem
        key={product.id_descuento}
        product={product}
        onAddToCartClicked={() => addToCart(product.id_descuento)} />,
    )}
  </div>
)

const mapStateToProps = state => ({
  //ff:getGeolocation(),
  products: getVisibleProducts(state.products),  
  type: getType(state),
  loc:getLocation(state)
})

export default connect(
  mapStateToProps,  
  { addToCart }
)(ProductsContainer)



class Root extends Component{
 
    render()
    {
      
      return(
        <Provider store={store}>
            <BrowserRouter basename={'/'}>
                <Switch>
                    <Route exact path={`${process.env.PUBLIC_URL}/`} component={App}/>
                    <Route exact path={`${process.env.PUBLIC_URL}/carro`} component={Pay}/>
                </Switch>
            </BrowserRouter>
        </Provider>)
     // document.getElementById('root')

    }
}
 ReactDOM.render(<Root/>, document.getElementById('root'));
