import { getTotal } from '../reducers'
import { getProduct } from '../reducers/products'
import { connect } from 'react-redux'


export const addToLocalStorageCart =  (value) => {
	console.log("lo agrega");
	var existing = localStorage.getItem("cart4");
	existing = existing ? JSON.parse(existing) : {};
	existing[value] = (existing[value] || 0) + 1;
	localStorage.setItem("cart4", JSON.stringify(existing));
};

export const getLocalStorageCart =  () => {
	console.log('papapapappapapa');
	var idproducts = localStorage.getItem("cart4");
	idproducts = idproducts ? JSON.parse(idproducts) : {};
	console.log("getLocalStorageCart");
	return idproducts;

};

export const removeItemLocalStorageCart =  (key) => {
	var idproducts = localStorage.getItem("cart4");
	idproducts = idproducts ? JSON.parse(idproducts) : {};
	delete idproducts[key];
	localStorage.setItem("cart4", JSON.stringify(existing));
};
